import React from 'react';
import logo from './logo.svg';
import './App.css';
import paginationFactory from 'react-bootstrap-table2-paginator';
import BootstrapTable from 'react-bootstrap-table-next';


const columns = [{
  dataField: 'id',
  text: 'Product ID'
}, {
  dataField: 'name',
  text: 'Product Name'
}, {
  dataField: 'price',
  text: 'Product Price'
}];

const options = {
  custom: true,
  totalSize: products.length
};

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      pag: 1,
      biere: [],
    }

    this.getBiere = this.getBiere.bind(this)
  }

  getBiere() {
    let array = [];
    const pag = this.state.pag
    fetch("https://api.punkapi.com/v2/beers?page=" + pag, {
      method: 'GET'
    }).then((Response) => Response.json())
      .then((Response) => {
        console.log("hello" + Response)
        for (let i = 0; i < Object.keys(Response).length; i++) {
          array.push(Response[i]);
        }

        this.setState({
          biere: array,
        })
      })
  }

  componentDidMount() {
    this.getBiere()
  }

  renderBiere() {
    const { value, volume, amertume } = this.state

    console.log(this.state.biere)

    return this.state.biere.map((item) => {
      return (
        <div key={item}>{item.name}<button>Check Biere</button></div>
      )
    })
  }

  render() {
    return (
      <div>
        {this.renderBiere()};
        <BootstrapTable keyField='id' data={this.state.biere} columns={columns} pagination={paginationFactory()} />
      </div>
    )
  }
}

export default App;
