import React from 'react';
import logo from './logo.svg';
import './App.css';
import paginationFactory, { PaginationProvider, PaginationListStandalone } from 'react-bootstrap-table2-paginator';
import BootstrapTable from 'react-bootstrap-table-next';  
import { throwStatement } from '@babel/types';
import filterFactory, { textFilter, Comparator } from 'react-bootstrap-table2-filter';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'; 
import 'react-bootstrap-table2-toolkit/dist/react-bootstrap-table2-toolkit.min.css';
import cellEditFactory from 'react-bootstrap-table2-editor';
import PropTypes from 'prop-types';


const selectOptions = {
  0: 25,
  1: 30,
  2: 40
};

const columns = [{
  dataField: 'name',
  mode: 'click',
  text: 'Nom de la biere',
  filter: textFilter()
},
{
  dataField: 'ibu',
  text: 'Amertume',
  filter: textFilter()
},
{
  dataField: 'abv',
  text: "Alcool",
  filter: textFilter()
  // formatter: cell => selectOptions[cell],
  // filter: selectFilter({
  //   options: selectOptions,
  //   defaultValue: 0
  // })
},
];


const RemoteAll = ({ data, page, sizePerPage, onTableChange, totalSize }) => (
  <div>
    <BootstrapTable
      remote
      keyField="id"
      data={data}
      columns={columns}
      filter={filterFactory()}
      pagination={paginationFactory({ page, sizePerPage, totalSize, hideSizePerPage: true, })}
      onTableChange={onTableChange}
      />
  </div>
);

RemoteAll.propTypes = {
  data: PropTypes.array.isRequired,
  page: PropTypes.number.isRequired,
  totalSize: PropTypes.number.isRequired,
  sizePerPage: PropTypes.number.isRequired,
  onTableChange: PropTypes.func.isRequired
};

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      page: 6 ,
      biere: [],
      sizePerPage: 80,
      totalSize: 350,
    }

    this.getBiere = this.getBiere.bind(this)
    this.handleTableChange = this.handleTableChange.bind(this)
  }

  getBiere(page) {
    fetch("https://api.punkapi.com/v2/beers?page=" + page + "&per_page=80", {
      method: 'GET'
    }).then((Response) => Response.json())
      .then((Response) => {
        this.setState({
          biere: Response,
        })
      })
  }

  componentDidMount() {
    this.getBiere(1)
  }

  handleTableChange = (type, { filters, page, sortField }) => {
    let result = this.state.biere;
    result = result.filter((row) => {
      console.log(row)
      let valid = true;
      for (const dataField in filters) {
        const { filterVal, filterType, comparator } = filters[dataField];

        if (filterType === 'TEXT') {
          if (comparator === Comparator.LIKE) {
            console.log(dataField)
            if (row[dataField] != null) {
              valid = row[dataField].toString().indexOf(filterVal) > -1;
            }
            console.log(row[dataField])
          } else {
            valid = row[dataField] === filterVal;
          }
        }
        if (!valid) break;
      }
      return valid;
    });
    this.getBiere(page);
  }


  render() {

    const { biere, page, sizePerPage, totalSize } = this.state;

    return (
      <div>
        <RemoteAll
        data={ biere }
        page={ page }
        sizePerPage={ sizePerPage }
        totalSize={ totalSize }
        onTableChange={this.handleTableChange}
        />
      </div>
    )
  }
}


export default App;
