import React from 'react';
import logo from './logo.svg';
import './App.css';
import paginationFactory from 'react-bootstrap-table2-paginator';
import BootstrapTable from 'react-bootstrap-table-next';  

const columns = [{
  dataField: 'id',
  text: 'Product ID'
}, {
  dataField: 'name',
  text: 'Product Name'
}, 
{
    dataField: 'boil_volume.value',
  text: "Product volume"
}
];

const options = {
  paginationSize: 4,
  pageStartIndex: 0,
  // alwaysShowAllBtns: true, // Always show next and previous button
  // withFirstAndLast: false, // Hide the going to First and Last page button
  // hideSizePerPage: true, // Hide the sizePerPage dropdown always
  // hidePageListOnlyOnePage: true, // Hide the pagination list when only one page
  firstPageText: 'First',
  prePageText: 'Back',
  nextPageText: 'Next',
  lastPageText: 'Last',
  nextPageTitle: 'First page',
  prePageTitle: 'Pre page',
  firstPageTitle: 'Next page',
  lastPageTitle: 'Last page',
  showTotal: true,
  paginationTotalRenderer: customTotal,
  sizePerPageList: [{
    text: '5', value: 5
  }, {
    text: '10', value: 10
  }, {
    text: 'All', value: products.length
  }] // A numeric array is also available. the purpose of above example is custom the text
};


class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      pag: 1,
      biere: [],
    }

    this.getBiere = this.getBiere.bind(this)
  }

  getBiere() {
    let array = [];
    const pag = this.state.pag
    fetch("https://api.punkapi.com/v2/beers?page=" + pag, {
      method: 'GET'
    }).then((Response) => Response.json())
      .then((Response) => {
        console.log("hello" + Response)
        for (let i = 0; i < Object.keys(Response).length; i++) {
          array.push(Response[i]);
        }

        this.setState({
          biere: array,
        })
      })
  }

  componentDidMount() {
    this.getBiere()
  }

  renderBiere() {
    const { value, volume, amertume } = this.state

    console.log(this.state.biere)

    return this.state.biere.map((item) => {
      return (
        <div key={item}>{item.name}<button>Check Biere</button></div>
      )
    })
  }

  handleNextPage = ({
    page,
    onPageChange
  }) => () => {
    onPageChange(page + 1);
  }

  handlePrevPage = ({
    page,
    onPageChange
  }) => () => {
    onPageChange(page - 1);
  }

  handleSizePerPage = ({
    page,
    onSizePerPageChange
  }, newSizePerPage) => {
    onSizePerPageChange(newSizePerPage, page);
  }

  render() {
    return (
          <div>
            <BootstrapTable
              keyField="id"
              data={this.state.biere}
              columns={columns} 
              pagination={paginationFactory(options)}
            />
          </div>
  )
  }
}

export default App;
