import React from 'react';
import logo from './logo.svg';
import './App.css';
import paginationFactory, { PaginationProvider, PaginationListStandalone } from 'react-bootstrap-table2-paginator';
import BootstrapTable from 'react-bootstrap-table-next';  
import { throwStatement } from '@babel/types';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';

const columns = [{
  dataField: 'id',
  text: 'Product ID'
}, {
  dataField: 'name',
  text: 'Product Name'
}, 
{
    dataField: 'boil_volume.value',
  text: "Product volume",
  filter: textFilter()
},
];

const RemotePagination = ({ data, page, sizePerPage, onTableChange, totalSize }) => (
  <div>
    <PaginationProvider
      pagination={
        paginationFactory({
          custom: true,
          page,
          sizePerPage,
          totalSize
        })
      }
      >
        {
          ({
            paginationProps
          }) => (
            <div>
              <div>
                <p>Current Page: { paginationProps.page }</p>
                <p>Current sizePerPage: [paginationProps.sizePerPage</p>
              </div>
              <div>
                <PaginationListStandalone
                  { ...paginationProps}
                  />
              </div>
              <BootstrapTable
                remote
                keyField="id"
                data={data}
                columns={columns}
                pagination={paginationFactory({ page, sizePerPage, totalSize })}
                onTableChange={onTableChange}
                />
            </div>
          )
        }
        </PaginationProvider>
  </div>
);


class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      page: 6 ,
      pag: 1,
      biere: [],
      sizePerPage: 80,
      totalSize: 350,
    }

    this.getBiere = this.getBiere.bind(this)
  }

  getBiere() {
    const pag = this.state.pag
    fetch("https://api.punkapi.com/v2/beers?page=" + pag + "&per_page=80", {
      method: 'GET'
    }).then((Response) => Response.json())
      .then((Response) => {
        this.setState({
          biere: Response,
        })
      })
  }

  componentDidMount() {
    this.getBiere()
  }

  handleTableChange = (type, { page, sizePerPage }) => {
      console.log(page)
      setTimeout(() => { 
        this.setState({
        pag: page,
      });
    }, 2000);
      console.log(page)
    this.getBiere();
  }

  render() {

    const onTableChange = (type, newState) => {

    }   
    return (
      <RemotePagination
      data={this.state.biere}
      page={this.state.page}
      sizePerPage={this.state.sizePerPage}
      totalSize={this.state.totalSize}
      onTableChange={this.handleTableChange}
      />
    )
  }
}


export default App;
