  import React from 'react';
import logo from './logo.svg';
import './App.css';
import paginationFactory, { PaginationProvider, PaginationListStandalone } from 'react-bootstrap-table2-paginator';
import BootstrapTable from 'react-bootstrap-table-next';  
import { throwStatement } from '@babel/types';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'; 
import 'react-bootstrap-table2-toolkit/dist/react-bootstrap-table2-toolkit.min.css';
import ToolkitProvider, { Search } from 'react-bootstrap-table2-toolkit';

const { SearchBar } = Search;

const columns = [{
  dataField: 'name',
  text: 'Nom de la biere',
}, 
{
  dataField: 'boil_volume.value',
  text: "Volume en litre",
},
];


const RemotePagination = ({ data, page, sizePerPage, onTableChange, totalSize }) => (
  <div>
    <BootstrapTable
      remote
      keyField="id"
      data={data}
      columns={columns}
      filter={filterFactory()}
      pagination={paginationFactory({ page, sizePerPage, totalSize, hideSizePerPage: true, })}
      onTableChange={onTableChange}
      />
  </div>
);


class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      page: 6 ,
      biere: [],
      sizePerPage: 80,
      totalSize: 350,
    }

    this.getBiere = this.getBiere.bind(this)
  }

  getBiere(page) {
    fetch("https://api.punkapi.com/v2/beers?page=" + page + "&per_page=80", {
      method: 'GET'
    }).then((Response) => Response.json())
      .then((Response) => {
        this.setState({
          biere: Response,
        })
      })
  }

  componentDidMount() {
    this.getBiere(1)
  }

  handleTableChange = (type, { filters, page }) => {
    this.getBiere(page);
  }

  render() {

    return (
      <div>
        <ToolkitProvider 
        keyField="id"
        data={this.state.biere}
        columns={columns}
        search>
          {
            props => (
              <div>
                <h3>Input something at below input field:</h3>
                <SearchBar {...props.searchProps} />
                <hr />
                <BootstrapTable
                  {...props.baseProps}
                />
              </div>
            )
          }
        </ToolkitProvider>
        <RemotePagination
        data={this.state.biere}
        page={this.state.page}
        sizePerPage={this.state.sizePerPage}
        totalSize={this.state.totalSize}
        onTableChange={this.handleTableChange}
        />
      </div>
    )
  }
}


export default App;
