import React from 'react';
import logo from './logo.svg';
import './App.css';
import paginationFactory from 'react-bootstrap-table2-paginator';
import BootstrapTable from 'react-bootstrap-table-next';


const columns = [{
  dataField: 'id',
  text: 'Product ID'
}, {
  dataField: 'name',
  text: 'Product Name'
}, 
{
    dataField: 'boil_volume.value',
  text: "Product volume"
}
];

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      pag: 1,
      biere: [],
    }

    this.getBiere = this.getBiere.bind(this)
  }

  getBiere() {
    let array = [];
    const pag = this.state.pag
    fetch("https://api.punkapi.com/v2/beers?page=" + pag, {
      method: 'GET'
    }).then((Response) => Response.json())
      .then((Response) => {
        console.log("hello" + Response)
        for (let i = 0; i < Object.keys(Response).length; i++) {
          array.push(Response[i]);
        }

        this.setState({
          biere: array,
        })
      })
  }

  componentDidMount() {
    this.getBiere()
  }

  renderBiere() {
    const { value, volume, amertume } = this.state

    console.log(this.state.biere)

    return this.state.biere.map((item) => {
      return (
        <div key={item}>{item.name}<button>Check Biere</button></div>
      )
    })
  }

  handleNextPage = ({
    page,
    onPageChange
  }) => () => {
    onPageChange(page + 1);
  }

  handlePrevPage = ({
    page,
    onPageChange
  }) => () => {
    onPageChange(page - 1);
  }

  handleSizePerPage = ({
    page,
    onSizePerPageChange
  }, newSizePerPage) => {
    onSizePerPageChange(newSizePerPage, page);
  }

  render() {
    const options = {
      custom: true,
      totalSize: this.state.biere.length
    };
    return (
      <div>
        <PaginationProvider
          pagination={paginationFactory(options)}
        >
          {
            ({
              paginationProps,
              paginationTableProps
            }) => (
                <div>
                  <div>
                    <p>Current Page: {paginationProps.page}</p>
                    <p>Current SizePerPage: {paginationProps.sizePerPage}</p>
                  </div>
                  <div className="btn-group" role="group">
                    <button className="btn btn-primary" onClick={this.handleNextPage(paginationProps)}>Next Page</button>
                    <button className="btn btn-success" onClick={this.handlePrevPage(paginationProps)}>Prev Page</button>
                    <button className="btn btn-danger" onClick={() => this.handleSizePerPage(paginationProps, 10)}>Size Per Page: 10</button>
                    <button className="btn btn-warning" onClick={() => this.handleSizePerPage(paginationProps, 25)}>Size Per Page: 25</button>
                  </div>
                  <BootstrapTable
                    keyField="id"
                    data={products}
                    columns={columns}
                    {...paginationTableProps}
                  />
                </div>
              )
          }
        </PaginationProvider>
        <Code>{sourceCode}</Code>
      </div>
    )
  }
}

export default App;
