import React from 'react';
import logo from './logo.svg';
import './App.css';
import paginationFactory from 'react-bootstrap-table2-paginator';
import BootstrapTable from 'react-bootstrap-table-next';  
import { throwStatement } from '@babel/types';

const columns = [{
  dataField: 'id',
  text: 'Product ID'
}, {
  dataField: 'name',
  text: 'Product Name'
}, 
{
    dataField: 'boil_volume.value',
  text: "Product volume"
}
];



class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      pag: 1,
      biere: [],
    }

    this.getBiere = this.getBiere.bind(this)
  }

  getBiere() {
    let array = [];
    const pag = this.state.pag
    fetch("https://api.punkapi.com/v2/beers?page=" + pag + "&per_page=80", {
      method: 'GET'
    }).then((Response) => Response.json())
      .then((Response) => {
        console.log("hello" + Response)
        for (let i = 0; i < Object.keys(Response).length; i++) {
          array.push(Response[i]);
        }

        this.setState({
          biere: array,
        })
      })
  }

  componentDidMount() {
    this.getBiere()
  }

  renderBiere() {
    const { value, volume, amertume } = this.state


    return this.state.biere.map((item) => {
      return (
        <div key={item.name}>{item.name}<button>Check Biere</button></div>
      )
    })
  }

  render() {
    // const pageButtonRenderer = ({
    //   page,
    //   onPageChange
    // }) => {
    //   const handleClick = (e) => {
    //     e.preventDefault();
    //     onPageChange(page);
    //   };
    // }
    // const options = {
    //   paginationSize: 5,
    //   pageStartIndex: 1,
    //   sizePerPage: 80,
    //   showTotal: true,
    const pageListRenderer = ({
      pages,
      onPageChange
    }) => {
      const pageWithoutIndication = pages.filter(p => typeof p.page !== 'string');
      return (
        <div>
          {
            pageWithoutIndication.map(p => (
              <button className="btn btn-success" onClick={() => onPageChange(p.page)}>{p.page}</button>
            ))
          }
        </div>
      );
    };

    const options = {
      pageListRenderer,
      onPageChange: (page) => {
        console.log('Newest page:' + page);
        this.setState({
          pag: page
        })
        this.getBiere();
      }
    };
    return (
      < BootstrapTable
        keyField="id"
        data={this.state.biere}
        columns={columns}
        pagination={paginationFactory(options)}
      />
    )
  }
}


export default App;
